import Navbar from "./components/navbar/Navbar";
import { Routes, Route } from "react-router-dom";
import Home from "./components/home/Home";
import BookReview from "./components/bookReview/BookCard";
import Login from "./components/login/Login";
import AddBook from "./components/AddBook/AddBook";

function App() {
  return (
   <div>
    <Navbar/>
    <Switch>

        <Route path="/reviews">
          <BookReview />
        </Route>

        <Route path="/add_books">
          <AddBook/>
        </Route>

        <Route path="/login">
          <Login/>
        </Route>

        <Route path="/">
          <Home />
        </Route>

    </Switch>
    <Routes>
        <Route
          path="/reviews"
          element={
            <BookReview />
          }
        />
        <Route
          path="/electronics"
          element={<Electronics products={products} cartIt={addToCart} />}
        />
        <Route
          path="/fashion"
          element={<Fashion products={products} cartIt={addToCart} />}
        />
        <Route
          path="/shoes"
          element={<Shoes products={products} cartIt={addToCart} />}
        />
        <Route
          path="/groceries"
          element={<Groceries products={products} cartIt={addToCart} />}
        />
        <Route path="/cart" element={<Cart products={cart} />} />
      </Routes>
    </div>
  );
}

export default App;
